package ra.dev.controller;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.web.bind.annotation.*;import ra.dev.model.entity.SubImage;import ra.dev.model.service.SubImageService;@CrossOrigin(origins = "*")@RestController@RequestMapping("api/v1/sub_image")public class SubImageController {    @Autowired    private SubImageService subImageService;    @PostMapping    public SubImage createSubImage(@RequestBody SubImage subImage) {        String subInput = subImage.getSubLink();        SubImage subImageCrate = subImageService.findByID(subImage.getSubID());        subImageCrate.setSubID(subImage.getSubID());        subImageCrate.setSubLink(subInput);        subImageCrate.setProduct(subImageCrate.getProduct());        return subImageService.saveOrUpdate(subImageCrate);    }}