package ra.dev.model.dto.request.cart;import lombok.AllArgsConstructor;import lombok.Data;import lombok.NoArgsConstructor;import java.io.Serializable; @Data @AllArgsConstructor @NoArgsConstructorpublic class CartDTO implements Serializable {    private Integer cartId;     private String productName;     private Integer quantity;     private String note;     private Integer discount;     private Integer totalAmount;  public CartDTO(Integer cartId, String productName, Integer quantity, String note, Integer discount, Integer totalAmount) {   this.cartId = cartId;   this.productName = productName;   this.quantity = quantity;   this.note = note;   this.discount = discount;   this.totalAmount = totalAmount;  } }