package ra.dev.model.dto.request.product;import lombok.Getter;import lombok.NoArgsConstructor;import lombok.Setter;import ra.dev.model.entity.SubImage;import java.util.ArrayList;import java.util.List;@Getter@Setter@NoArgsConstructorpublic class ProductGet {    private Integer productID;    private String catalogName;    private String productName;    private Integer discount;    private Integer price;    private Boolean productStatus;    private String productImg;    private String description;    List<SubImage> listSub = new ArrayList<>();    public ProductGet(Integer productID, String catalogName, String productName, Integer discount, Integer price, Boolean productStatus, String productImg, String description, List<SubImage> listSub) {        this.productID = productID;        this.catalogName = catalogName;        this.productName = productName;        this.discount = discount;        this.price = price;        this.productStatus = productStatus;        this.productImg = productImg;        this.description = description;        this.listSub = listSub;    }}