package ra.dev.model.repository;import org.springframework.data.domain.Page;import org.springframework.data.domain.Pageable;import org.springframework.data.jpa.repository.JpaRepository;import org.springframework.data.jpa.repository.Query;import org.springframework.stereotype.Repository;import ra.dev.model.dto.request.product.ProductCreat;import ra.dev.model.entity.Product;import java.util.List;@Repositorypublic interface ProductRepository extends JpaRepository<Product, Integer> {    ProductCreat save(ProductCreat product);    List<Product> findByProductNameContaining(String productName);    @Query(value = "select o from Product o where o.productStatus = true ")    List<Product> getAllProductActive();    @Query(value = "select o from Product o where o.productStatus = false ")    List<Product> getAllProductInactive();    @Query(value = "select o from Product o where o.productStatus = true ")    Page<Product> getAllPagingActive(Pageable pageable);}